import {ReactDom} from 'react';
import React,{useState,useEffect} from 'react';

const MoviePage=()=>{
    
    const [showText, setShowText] = useState(false)
  const handleMouseEnter = e => {
    e.target.style.background = "grey"
    setShowText(true)
  }
  const handleMouseLeave = e => {
    e.target.style.background = "maroon"
    setShowText(false)
  }
    return (
        <div >
            <h1 style={{color: "red" , alignContent:"center"} }>BEST KIDS MOVIE </h1>
            <div className='rowC'>
            <div className='img' onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}>
                 <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQym1gdYXZTzLBSZJH4bWsOw0pfave0id7zYw&usqp=CAU"></img>
                 {showText && <p>Now you can see me!</p>}
                </div>
                <div className='img' >
                 <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQym1gdYXZTzLBSZJH4bWsOw0pfave0id7zYw&usqp=CAU"></img>
                </div>
                       
                </div>
            
        </div>
    )
}

export default MoviePage;
