import React, { useState} from 'react';

const MoviePage1 = () => {

  const [showText, setShowText] =useState(false);
  const handleMouseEnter = () => {
    setShowText(true);

  }
  const handleMouseLeave = () => {

    setShowText(false);
  }
  return (
    <div  >
      <h1>BEST KIDS MOVIE OF THE DECADE</h1>
      <div className='rowC'>
        <div className='myPoster' onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}>
          <img className='img' src="https://bit.ly/3G0RJXR"></img>
          {showText && <div class="text">MINIONS <br/>2 hr 05 min <br/>OTT:AMAZON PRIME</div>}
        </div>
        <div className='myPoster' onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}>
          <img className='img' src="https://bit.ly/3WuQWUB"></img>
          {showText && <div class="text"><br/>THE SEA BEAST <br></br> 1 hr 55 min<br></br>OTT: NETFLIX</div>}
        </div>
        <div className='myPoster' onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}>
          <img className='img' src="https://bit.ly/3PBZWVr"></img>
          {showText && <div class="text"><br/>LIGHT YEAR <br></br> 2 hr 55 min <br></br>OTT: DISNEYPLUS</div>}
        </div>
      </div>
      <div className='rowC'>
        <div className='myPoster' onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}>
          <img className='img' src="https://bit.ly/3YxZLig" alt="photo"></img>
          {showText && <div class="text"><br></br>WISH DRAGON <br></br> 1 hr 55 min<br></br>OTT: NETFLIX</div>}
        </div>
        <div className='myPoster' onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}>
          <img className='img' src="https://bit.ly/3VeOkcw" alt="photo"></img>
          {showText && <div class="text"><br></br>BAY MAX <br></br> 1 hr 55 min <br></br>OTT: SONY LIV</div>}
        </div>
        <div className='myPoster' onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}>
          <img className='img' src="https://bit.ly/3G115TC" alt="photo"></img>
          {showText && <div class="text">HOTEL TRANSYLVANIA <br></br> 1 hr 55 min <br></br>OTT:DISNEY<br></br></div>}

        </div>

      </div>
    </div>
  )
}
export default MoviePage1;
